def my_aggregate(df, grouping_column, agg_operation, agg_columns):
    # exprs = {col_name: "last" for col_name in df.columns}
    # df = df.groupBy("Time").agg(exprs)

    arguments = [agg_operation(col(col_name)).alias(col_name) for col_name in agg_columns]
    df = df.groupBy(grouping_column).agg(*arguments)

    return df
