DROP EXTERNAL TABLE mySchema.Metadata_Key_Cols
CREATE EXTERNAL TABLE mySchema.Metadata_Key_Cols (
     Table_Name VARCHAR(MAX), 
     Name  VARCHAR(MAX),
     English_Name  VARCHAR(MAX)
) WITH (
         DATA_SOURCE = myStagingContainer,
         LOCATION = 'Metadata/Key_Cols_Silver/*.parquet',
         FILE_FORMAT = myParquetFormat
);
